<script>
export default {
  name: "offers-page",

  data() {
    return {
      banks: {
        cs: {
          progress: undefined,
        },
        alfa: {
          status: undefined,
          progress: undefined,
        },
        mtb: {
          status: undefined,
          progress: undefined,
        },
        vtb: {
          status: undefined,
          progress: undefined,
        },
        belinvest: {
          status: undefined,
          progress: undefined,
        },
        dabrabyt: {
          status: undefined,
          progress: undefined,
        },
        belweb: {
          status: undefined,
          progress: undefined,
        },
        reshenie: {
          status: undefined,
          progress: undefined,
        },
        paritet: {
          status: undefined,
          progress: undefined,
        },
      },
    };
  },
  created() {
    this.banks.cs.progress = this.getRandomPercent(83, 98);
    // this.getRandomInt(0, 2) === 1
    //   ? (this.banks.alfa.status = true) && (this.banks.mtb.status = true)
    //   : (this.banks.alfa.status = false) && (this.banks.mtb.status = false);
    if (this.getRandomInt(0, 3) === 0) {
      this.banks.vtb.status = true;
      this.banks.belinvest.status = true;
    } else if (this.getRandomInt(0, 3) === 1) {
      this.banks.alfa.status = true;
      this.banks.mtb.status = true;
    } else if (this.getRandomInt(0, 3) === 2) {
      this.banks.dabrabyt.status = true;
      this.banks.belweb.status = true;
    } else {
      this.banks.reshenie.status = true;
      this.banks.paritet.status = true;
    }
    this.banks.paritet.progress = this.getRandomPercent(22, 51);
    this.banks.reshenie.progress = this.getRandomPercent(22, 51);
    this.banks.belweb.progress = this.getRandomPercent(22, 51);
    this.banks.dabrabyt.progress = this.getRandomPercent(22, 51);
    this.banks.belinvest.progress = this.getRandomPercent(22, 51);
    this.banks.vtb.progress = this.getRandomPercent(22, 51);
    this.banks.alfa.progress = this.getRandomPercent(22, 51);
    this.banks.mtb.progress = this.getRandomPercent(22, 51);
  },

  methods: {
    getRandomPercent(min, max) {
      return Math.random().toFixed(1) * (max - min) + min;
    },

    getRandomInt(min, max) {
      min = Math.ceil(min);
      max = Math.floor(max);
      return Math.floor(Math.random() * (max - min)) + min;
    },

    getBg(percent) {
      if (percent >= 80) {
        return "green";
      } else if (percent < 80 && percent >= 60) {
        return "yellow";
      } else if (percent < 60 && percent >= 40) {
        return "orange";
      } else if (percent < 40) {
        return "red";
      }
    },
  },
};
</script>

